      ; Reset Vector
      org   0000h
      jmp   50H

;====================================================================
; CODE SEGMENT
;====================================================================

     org   50H; main
Start:	
     Mov TMOD, #05 ;   Used Timer0 as Counter in mode 1
     mov TL0, #0
     mov TH0, #0
     SETB TR0 ;           Start Timer
Show: 
      mov P2, TL0 ;      Copied Timer0 register value in P2
      jmp Show;           Continuously Copy Timer0 register value in P2
